import React , {Component} from 'react'
import AliceCarousel from 'react-alice-carousel'
import 'react-alice-carousel/lib/alice-carousel.css'
import styled from "styled-components";


const ScrollElement = styled.div`
   background: black;
   padding: 6%;
   height: 100vh;
   overflow: hidden;

   .alice-carousel {
    box-sizing: border-box;
    position: relative;
    width: 90%;
    margin: auto;
  }

`;

export class Gallery extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentIndex: 0,
            itemsInSlide: 1,
            responsive: {0: {items: 3}},
            galleryItems: [],
        }
    }
    componentWillMount() {
        let url = 'http://api.giphy.com/v1/gifs/search?q=${term}&api_key=IrmohbMKzmTvBaN5pvQXzuXhC4zEIy8P';
        fetch(url).then(response => response.json()).then((d) => {
            this.setState({
                galleryItems: d.data
            });
        });
    }

    render() {
        const {galleryItems, responsive} = this.state;
        return (
            <ScrollElement>
                <AliceCarousel
                    items={galleryItems.map(d => <iframe src={d.embed_url}/>)}
                    responsive={responsive}
                    dotsDisabled={true}
                    buttonsDisabled={false}
                />
            </ScrollElement>
        )
    }
}
