import React , {Component} from 'react'
import AliceCarousel from 'react-alice-carousel'
import 'react-alice-carousel/lib/alice-carousel.css'
import styled from "styled-components";


const ScrollElement = styled.div`
   background: black;
   padding: 6%;
   height: 100vh;
   overflow: hidden;

   .alice-carousel {
    box-sizing: border-box;
    position: relative;
    width: 90%;
    margin: auto;
  }

   .alice-carousel__wrapper {
    position: relative;
    width: 100%;
    height: 400px;
    box-sizing: border-box;
    overflow: hidden;
  }

  .alice-carousel__stage-item {
    width: 800px;
    height: 100%;
    position: relative;
    display: inline-block;
    vertical-align: top;
    white-space: normal;
    line-height: 0;
} 
   .alice-carousel__play-btn {
    display: inline-block;
    position: absolute;
    top: 75%;
    left: 20px;
}
`;

export class Gallery extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentIndex: 0,
            itemsInSlide: 1,
            responsive: {0: {items: 3}},
            galleryItems: [],
        }
    }
    componentWillMount() {
        let url = 'http://api.giphy.com/v1/gifs/search?q=${term}&api_key=IrmohbMKzmTvBaN5pvQXzuXhC4zEIy8P';
        fetch(url).then(response => response.json()).then((d) => {
            this.setState({
                galleryItems: d.data
            });
        });
    }

    render() {
        const {galleryItems, responsive} = this.state;
        return (
            <ScrollElement>
                <AliceCarousel
                    duration={400}
                    autoPlay={true}
                    startIndex = {0}
                    fadeOutAnimation={true}
                    playButtonEnabled={true}
                    responsive={responsive}
                    autoPlayInterval={2000}
                    autoPlayDirection="ltr"
                    autoPlayActionDisabled={true}
                    items={galleryItems.map(gal => <iframe src={gal.embed_url} height={300} width={300}/>)}
                />
            </ScrollElement>
        )
    }
}
