import React , {Component} from 'react'
import AliceCarousel from 'react-alice-carousel'
import 'react-alice-carousel/lib/alice-carousel.css'

export class Gallery extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentIndex: 0,
            itemsInSlide: 1,
            responsive: {0: {items: 3}},
            galleryItems: [],
        }
    }
    componentWillMount() {
        let url = 'http://api.giphy.com/v1/gifs/search?q=${term}&api_key=IrmohbMKzmTvBaN5pvQXzuXhC4zEIy8P';
        fetch(url).then(response => response.json()).then((d) => {
            this.setState({
                galleryItems: d.data.embed_url
            });
        });
    }

    slidePrevPage = () => {
        const currentIndex = this.state.currentIndex - this.state.itemsInSlide
        this.setState({currentIndex})
    }

    slideNextPage = () => {
        const {itemsInSlide, galleryItems: {length}} = this.state
        let currentIndex = this.state.currentIndex + itemsInSlide
        if (currentIndex > length) currentIndex = length

        this.setState({currentIndex})
    }

    handleOnSlideChange = (event) => {
        const {itemsInSlide, item} = event
        this.setState({itemsInSlide, currentIndex: item})
    }

    render() {
        const {currentIndex, galleryItems, responsive} = this.state

        return (
            <div>
                <AliceCarousel
                    items={galleryItems}
                    slideToIndex={currentIndex}
                    responsive={responsive}
                    onInitialized={this.handleOnSlideChange}
                    onSlideChanged={this.handleOnSlideChange}
                    onResized={this.handleOnSlideChange}
                />
            </div>
        )
    }
}
